INSERT INTO `check_report_question_advice` VALUES (1,'vascular',2,0,'{\"question_1\": {\"title\": \"血管风险显示危险意味着什么？\", \"content\": \"首先这说明您的视网膜血管已经出现了堵塞和出血，可能伴随着视力下降和视野缩小的症状，需要及时就医治疗，及时的治疗有助于及时改善血管堵塞的情况，恢复血供，从而防止不良预后的发生。\"}, \"question_2\": {\"title\": \"您现阶段的风险可能是什么原因引起的？\", \"content\": \"从您的整体情况来看，出现微血管堵塞的主要原因是您的糖尿病引起的，糖尿病会引起血液粘稠度的增高，并且可能会导致血栓的形成，目前血栓堵住了视网膜的血管，需要立即就医。\"}}','[{\"title\": \"立即就医\", \"content\": \"建议您至三甲医院的眼科进行进一步检查，按照目前国内眼科的情况，普通二级医院的眼科可能无法处理此类问题。\"}]','2022-01-06 17:18:19','1971-01-01 00:00:00'),(2,'vascular',2,0,'{\"question_1\": {\"title\": \"血管风险显示危险意味着什么？\", \"content\": \"首先这说明您的视网膜血管已经出现了堵塞和出血，可能伴随着视力下降和视野缩小的症状，需要及时就医治疗，及时的治疗有助于及时改善血管堵塞的情况，恢复血供，从而防止不良预后的发生。\"}, \"question_2\": {\"title\": \"您现阶段的风险可能是什么原因引起的？\", \"content\": \"从您的整体情况来看，出现微血管堵塞的主要原因是您糖尿病控制不稳定引起的，血糖控制不稳定会加速血管的损伤，并且可能会导致血栓的形成，目前血栓堵住了视网膜的血管，需要立即就医。\"}}','[{\"title\": \"立即就医\", \"content\": \"建议您至三甲医院的眼科进行进一步检查，按照目前国内眼科的情况，普通二级医院的眼科可能无法处理此类问题。\"}]','2022-01-06 17:18:19','1971-01-01 00:00:00'),(3,'vascular',2,0,'{\"question_1\": {\"title\": \"血管风险显示危险意味着什么？\", \"content\": \"首先这说明您的视网膜血管已经出现了堵塞和出血，可能伴随着视力下降和视野缩小的症状，需要及时就医治疗，及时的治疗有助于及时改善血管堵塞的情况，恢复血供，从而防止不良预后的发生。\"}, \"question_2\": {\"title\": \"您现阶段的风险可能是什么原因引起的？\", \"content\": \"您的整体情况来看，出现微血管堵塞的主要原因是您长期的糖尿病引起的，糖尿病会引起血液粘稠度的增高，糖尿病病程越长，血液粘稠度就越高，并且可能会导致血栓的形成，目前血栓堵住了视网膜的血管，需要立即就医。\"}}','[{\"title\": \"立即就医\", \"content\": \"建议您至三甲医院的眼科进行进一步检查，按照目前国内眼科的情况，普通二级医院的眼科可能无法处理此类问题。\"}]','2022-01-06 17:18:19','1971-01-01 00:00:00'),(4,'vascular',2,0,'{\"question_1\": {\"title\": \"血管风险显示危险意味着什么？\", \"content\": \"首先这说明您的视网膜血管已经出现了堵塞和出血，可能伴随着视力下降和视野缩小的症状，需要及时就医治疗，及时的治疗有助于及时改善血管堵塞的情况，恢复血供，从而防止不良预后的发生。\"}, \"question_2\": {\"title\": \"您现阶段的风险可能是什么原因引起的？\", \"content\": \"从您的整体情况来看，出现微血管堵塞的主要原因是您长期的糖尿病控制不稳定引起的，血糖控制不稳定会加速血管的损伤，并且可能会导致血栓的形成，糖尿病病程越长，此类风险越高，目前血栓堵住了视网膜的血管，需要立即就医。\"}}','[{\"title\": \"立即就医\", \"content\": \"建议您至三甲医院的眼科进行进一步检查，按照目前国内眼科的情况，普通二级医院的眼科可能无法处理此类问题。\"}]','2022-01-06 17:18:20','1971-01-01 00:00:00'),(5,'vascular',2,0,'{\"qu