INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-黄斑视力损伤-低风险','macularVisualImpairment','macularVisualImpairment','[{"show": true,"type": "NAME","content": ["黄斑视力损伤风险"],"display_name": "风险名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的黄斑视力损伤风险为低风险，说明未发现任何结构异常改变。建议保持良好的用眼习惯并补充叶黄素等防蓝光物质，预防黄斑损伤。"],"display_name": "列表页建议"}]','[{"show":false,"type":"INTRO","content":["人类接受的信息中，90%都来自视觉，失明对人的工作和生活有极大影响。据统计，我国因视力低下而残疾的人数达1230万人，约近1分钟就会出现1个盲人、3个低视力患者。除了白内障、青光眼、年龄相关性黄斑病变、病理性近视等眼部疾病，糖尿病等全身慢病的并发症也会导致失明。"],"display_name":"简介"},{"show":false,"type":"FACTOR","content":[{"title":"近视尤其是高度近视。","content":"39%的高度近视人群在75岁之前有严重的视力损伤。"},{"title":"眼部病变家族史。","content":"如青光眼家族史。"},{"title":"高度用眼。","content":"经常使用电脑、手机等电子产品的人群，黄斑容易受到屏幕蓝光的损害。"},{"title":"40岁以上人群。","content":"青光眼、黄斑病变等致盲疾病的发生率较高。"}],"display_name":"引发原因"},{"show":false,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"补充叶黄素。","content":"具有黄斑变性风险的人群，每日可补充10毫克。"},{"title":"食用富含维生素A的食物。","content":"比如各种动物的肝脏、鱼肝油、奶类和蛋类，植物性的食物，比如胡萝卜、苋菜、菠菜、韭菜、青椒、红心白薯以及水果中的桔子、杏子、柿子等。"}]},{"type":"SPORT","title":"运动","content":[{"title":"高度近视者不能剧烈运动。","content":"高度近视者不能参加危险刺激的娱乐活动（例如蹦极、过山车等），避免视网膜脱落的风险。"}]},{"type":"EYE","title":"用眼","content":[{"title":"连续写字、阅读每40分钟须休息5-10分钟。","content":"避免眼睛过度疲劳。"},{"title":"不在晃动的车厢内阅读，不躺着看书。","content":""},{"title":"看电视时眼睛与电视保持屏幕尺寸约6~8倍的距离。","content":"每隔30分钟休息5~10分钟，连续看电视绝对不可以超过1小时。"},{"title":"不要在黑暗中看手机和电脑。","content":""}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":false,"type":"SYMPTOM","content":["视野缺失","视物变形","眼前黑点","视物模糊","眼痛眼胀"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]',0,0,'翁劲','翁劲','2022-06-23 21:13:44.0');
