INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-心脑血管-中低风险','cardiovascular','cardiovascular','[{"show": true,"type": "NAME","content": ["心脑血管风险"],"display_name": "名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的心脑血管风险为中低风险，建议引起关注。数据结果显示，中低风险人群未来10年突发心脑血管事件的概率为5%-10%"],"display_name": "列表页建议"}]','[{"show":true,"type":"INTRO","content":["心脑血管疾病是心血管疾病和脑血管疾病的统称，表现为大脑、心脏及全身组织发生缺血性或出血性病变。全国有心脑血管病患者3.3亿，心脑血管疾病发病突然，死亡率和致残率高，是我国居民健康的头号杀手。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"血管因素。","content":"大脑供血的血管出现狭窄、闭塞或动脉瘤等情况，引起脑组织缺血缺氧，诱发大脑功能障碍。高血压是最主要的危险因素。"},{"title":"既往心脏疾病。","content":"心脏疾病易诱发心肌缺血缺氧引起心肌梗塞。"},{"title":"代谢因素。","content":"糖类、脂类代谢异常，过度肥胖，会增加血液粘滞度，增加脑卒中风险。"},{"title":"生活习惯。","content":"熬夜、吸烟、酗酒等不良的生活习惯也会增加心脑血管风险。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"少盐。","content":"2013年中国因高盐饮食死亡约143万人，占全部死亡的15.6% 。"},{"title":"多吃蔬菜水果。","content":"每日吃蔬菜400-500g，水果100g。"},{"title":"少吃红肉。","content":"红肉包括猪肉、牛肉、羊肉等。"},{"title":"每周吃5个蛋类。","content":"每天增加1个鸡蛋的摄入，心血管病事件发生风险降低3%，但不要超过每周6个。"}]},{"type":"WEIGHT","title":"体重","content":[{"title":"控制BMI。","content":"成年人正常体质指数(BMI)为18.5-23.9，在24-27.9为超重，提示需要控制体重；BMI>28 为肥胖，需要减重。肥胖主要是通过血压影响脑卒中疾病的发生，BMI每增加5 kg/m2，相当于收缩压升高8.3 mmHg。"}]},{"type":"SPORT","title":"运动","content":[{"title":"休闲身体活动。","content":"进行中等强度休闲活动(如太极拳、跳舞、健身、步行等)可以让心血管病死亡风险下降14%。每快速步行1小时，心血管病死亡风险减少12%。"}]},{"type":"POLLUTION","title":"吸烟与空气污染","content":[{"title":"","content":"吸烟人群发生心脑血管疾病的风险比非吸烟人群显著提高。PM2.5 浓度每增加10μg/m3，心脑血管疾病发病风险上升明显。"}]},{"type":"OTHERS","title":"其它","content":[{"title":"","content":"高血压人群应谨慎泡温泉和蒸桑拿，在冬季要注意保暖。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["单侧肢体无力或麻木","眩晕呕吐","心绞痛","胸闷心悸"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]',0,0,'翁劲','翁劲','2022-05-16 21:03:31.0');
