INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-老年痴呆-高风险','dementia','dementia','[{"show": true,"type": "NAME","content": ["老年痴呆风险"],"display_name": "名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的老年痴呆风险为高风险，建议持续关注，定期监测。数据结果显示，高风险人群未来20年出现老年痴呆的概率为4%-16%"],"display_name": "列表页建议"}]','[{"show":true,"type":"INTRO","content":["老年痴呆又称为阿尔兹海默症，是发生于老年前期和老年的中枢神经退行性病变，主要表现为进行性认知功能和行为障碍，目前无法治愈，只能通过治疗减轻病情，延缓发展。我国目前有超过1000万阿尔兹海默患者。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"年龄增长。","content":"年龄是老年痴呆最大的危险因素，年龄每增长5-6岁，风险就会增加一倍。"},{"title":"遗传因素。","content":"有家族史或唐氏综合征的人群出现老年痴呆的概率更高，年龄更早。"},{"title":"不良生活方式。","content":"缺乏锻炼、吸烟、睡眠不足、高脂饮食等不良习惯会增加老年痴呆的风险。"},{"title":"教育水平和社交缺乏。","content":"低于高中教育水平以及缺乏社交同样是老年痴呆的危险因素。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"改变单一的饮食结构。","content":"食物可以多样化，蘑菇、西兰花、花菜、鱼类、鸡蛋、酸奶等都是不错的选择。"},{"title":"补充营养素。","content":"维生素C、叶酸、硒、硫辛酸、谷胱甘肽等抗氧化物质能够预防大脑的退行性变。"},{"title":"戒烟限酒。","content":"香烟中的尼古丁和酒中的醇类等物质，能够兴奋和麻痹大脑神经，大脑长期处于兴奋或麻痹状态，会出现功能受损。"}]},{"type":"SPORT","title":"运动","content":[{"title":"适量运动。","content":"保持良好的运动习惯，积极控制体重，维持腰部和腿部的运动功能。"},{"title":"保持手部运动。","content":"做菜、写日记、弹奏乐器、画画、打麻将、太极拳等能够预防痴呆。"}]},{"type":"POLLUTION","title":"生活习惯","content":[{"title":"积极社交。","content":"经常参加社交活动，丰富生活内容，降低孤独感，和家人、子女、朋友维持紧密的关系。"},{"title":"多动脑。","content":"经常阅读、看书、看报，保持思维的活跃，可以经常玩一些认知的小游戏。"},{"title":"保持充足的睡眠。","content":"早睡早起，避免长期精神紧张，保持轻松愉快的心情能够减少老年痴呆的发生。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["记忆障碍","失语","失用","人格和行为改变"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]',0,0,'翁劲','翁劲','2022-08-04 11:44:12.0');
INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-高血压-中高风险','presure','presure','[{"show": true,"type": "NAME","content": ["动脉硬化风险"],"display_name": "名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的动脉硬化风险为中高风险，建议引起重视。大数据统计结果显示，中高风险人群出现高血压的可能性为19.2%"],"display_name": "列表页建议"}]','[{"show":true,"type":"INTRO","content":["由于身体老化或身体机能的改变导致动脉壁弹性丢失，动脉弹性的改变是心脑血管功能发生改变的诱因，多见于40岁以上的中老年人，且呈年轻化趋势。血脂异常、血压异常、长期紧张、糖尿病、肥胖及部分自身免疫性疾病，也会引起血管压力变化，在视网膜上引发和高血压类似的血管压力变化表现，因此这些人群也会提示血管压力变化的风险。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"钠盐摄入过多。","content":"钠盐会引起血容量增加，诱发高血压。"},{"title":"超重和肥胖。","content":"肥胖容易引起血液粘稠度增高，增大对血管壁的压力。"},{"title":"长期精神紧张。","content":"引起交感神经过度兴奋诱发高血压。"},{"title":"遗传因素。","content":"高血压家族史会提高患高血压的概率。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"少吃盐。","content":"每天摄入食盐在6克以内，少吃含盐高的调味品。80%的中国人日均盐摄入量大于12.5克。"},{"title":"少吃肥肉和动物内脏。","content":"其它肉类每天不应超过1-2两。"},{"title":"少喝酒。","content":"高血压5%-10%是过量饮酒引起的。每日酒精摄入量男性不应超过25克；女性不应超过15克。"}]},{"type":"SPORT","title":"运动","content":[{"title":"每周保持中等强度运动4-7次。","content":"每次持续30-60分钟。每周应有1次以上的有氧运动，如步行、慢跑、骑车、游泳、做健美操、跳舞和非比赛性划船等。"}]},{"type":"WEIGHT","title":"体重","content":[{"title":"控制BMI。","content":"成年人正常体质指数(BMI)为18.5-23.9，在24-27.9为超重，提示需要控制体重；BMI>28为肥胖，需要减重。BMI>24的人群发生高血压的风险是体重正常者的3~4倍。"},{"title":"控制腰围。","content":"男性腰围大于90cm、女性腰围大于85cm者，发生高血压的风险是腰围正常者的4倍以上。"}]},{"type":"MOOD","title":"心理","content":[{"title":"缓解紧张情绪。","content":"人在紧张、愤怒、压抑、焦虑等状态下，血压上升。精神紧张者发生高血压的风险是正常人群的1.5倍左右。"}]},{"type":"POLLUTION","title":"吸烟与空气污染","content":[{"title":"不吸烟，减少被动吸烟。","content":"吸烟约占男性心血管疾病负担的1/3。"},{"title":"保持室内PM2.5在优良（<75μg/m³）。","content":"可使用空气净化器等设备。如果室外空气质量差，减少室外活动时间，做好防护。PM浓度每增加10μg/m3，高血压患病风险增加14%，超重和肥胖者风险更高。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["头晕","头痛","胸闷心慌","失眠","肢体麻木"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"},{"show":true,"type":"SYMPTOM","content":["北京","天津","上海","辽宁","云南"],"default_url":"https://img3.airdoc.com/cms/salesman/e8cd4394-388d-4d96-a949-7bab4875be4c.png","display_name":"高发地区"}]',0,0,'翁劲','翁劲','2022-04-30 11:10:17.0');
INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-高血压-中低风险','presure','presure','[{"show": true,"type": "NAME","content": ["动脉硬化风险"],"display_name": "名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的动脉硬化风险为中低风险，建议引起关注。大数据统计结果显示，中低风险人群出现高血压的可能性为8.7%"],"display_name": "列表页建议"}]','[{"show":true,"type":"INTRO","content":["由于身体老化或身体机能的改变导致动脉壁弹性丢失，动脉弹性的改变是心脑血管功能发生改变的诱因，多见于40岁以上的中老年人，且呈年轻化趋势。血脂异常、血压异常、长期紧张、糖尿病、肥胖及部分自身免疫性疾病，也会引起血管压力变化，在视网膜上引发和高血压类似的血管压力变化表现，因此这些人群也会提示血管压力变化的风险。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"钠盐摄入过多。","content":"钠盐会引起血容量增加，诱发高血压。"},{"title":"超重和肥胖。","content":"肥胖容易引起血液粘稠度增高，增大对血管壁的压力。"},{"title":"长期精神紧张。","content":"引起交感神经过度兴奋诱发高血压。"},{"title":"遗传因素。","content":"高血压家族史会提高患高血压的概率。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"少吃盐。","content":"每天摄入食盐在6克以内，少吃含盐高的调味品。80%的中国人日均盐摄入量大于12.5克。"},{"title":"少吃肥肉和动物内脏。","content":"其它肉类每天不应超过1-2两。"},{"title":"少喝酒。","content":"高血压5%-10%是过量饮酒引起的。每日酒精摄入量男性不应超过25克；女性不应超过15克。"}]},{"type":"SPORT","title":"运动","content":[{"title":"每周保持中等强度运动4-7次。","content":"每次持续30-60分钟。每周应有1次以上的有氧运动，如步行、慢跑、骑车、游泳、做健美操、跳舞和非比赛性划船等。"}]},{"type":"WEIGHT","title":"体重","content":[{"title":"控制BMI。","content":"成年人正常体质指数(BMI)为18.5-23.9，在24-27.9为超重，提示需要控制体重；BMI>28为肥胖，需要减重。BMI>24的人群发生高血压的风险是体重正常者的3~4倍。"},{"title":"控制腰围。","content":"男性腰围大于90cm、女性腰围大于85cm者，发生高血压的风险是腰围正常者的4倍以上。"}]},{"type":"MOOD","title":"心理","content":[{"title":"缓解紧张情绪。","content":"人在紧张、愤怒、压抑、焦虑等状态下，血压上升。精神紧张者发生高血压的风险是正常人群的1.5倍左右。"}]},{"type":"POLLUTION","title":"吸烟与空气污染","content":[{"title":"不吸烟，减少被动吸烟。","content":"吸烟约占男性心血管疾病负担的1/3。"},{"title":"保持室内PM2.5在优良（<75μg/m³）。","content":"可使用空气净化器等设备。如果室外空气质量差，减少室外活动时间，做好防护。PM浓度每增加10μg/m3，高血压患病风险增加14%，超重和肥胖者风险更高。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["头晕","头痛","胸闷心慌","失眠","肢体麻木"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"},{"show":true,"type":"SYMPTOM","content":["北京","天津","上海","辽宁","云南"],"default_url":"https://img3.airdoc.com/cms/salesman/e8cd4394-388d-4d96-a949-7bab4875be4c.png","display_name":"高发地区"}]',0,0,'翁劲','翁劲','2022-04-30 11:10:04.0');
