INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-贫血-高风险','anemia','anemia','[{"show": true,"type": "NAME","content": ["贫血风险"],"display_name": "名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的贫血风险为高风险，说明您的视网膜血管颜色偏淡，分析结果显示出现贫血的风险高，请注意干预调整。"],"display_name": "列表页建议"}]','[{"show":true,"type":"INTRO","content":["血气不足是指血液运输氧气的能力下降。常见于孕妇、成年女性、身体瘦弱的人群，血气不足使得身体各器官组织无法得到充分的氧供，可能导致机体功能的异常。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"膳食因素。","content":"不良的烹饪习惯导致铁、叶酸、维生素B12摄入不足容易诱发贫血。"},{"title":"化学物质。","content":"苯、除草剂、杀虫剂、染发剂的长期接触也容易诱发贫血。"},{"title":"出血。","content":"各种急性或慢性失血，外伤、女性月经量过大、消化道出血等会导致红细胞大量丢失。"},{"title":"其他因素。","content":"有艾滋病、病毒感染、恶性肿瘤和各种慢性病也会导致贫血。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"合理膳食。","content":"饮食保证食物的多样性，适当增加瘦肉、动物内脏、鱼肉、血制品的摄入。"},{"title":"多吃蔬菜水果。","content":"维生素C可以促进铁的吸收和红细胞的合成。"},{"title":"补充铁剂、叶酸和维生素B12。","content":"建议孕妇可适当补充叶酸和维生素B12。"}]},{"type":"SPORT","title":"运动","content":[{"title":"避免剧烈活动。","content":"贫血人群应注意休息，避免剧烈活动。"}]},{"type":"POLLUTION","title":"生活习惯","content":[{"title":"辐射防护。","content":"避免和高能射线X线、γ射线的长期接触，穿戴防护服。"},{"title":"减少化学物质接触。","content":"避免长期接触苯、除草剂、杀虫剂等，避免长期染发。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["皮肤苍白","感觉异常","头晕心悸","食欲缺乏","异食癖"],"default_url":"https://img3.airdoc.com/cms/salesman/79c25278-77cf-426d-9a3c-c3572421b077.png","display_name":"常见表现"},{"show":"true","type":"FACTOR","content":[{"title":"皮肤黏膜改变。","content":"贫血会导致皮肤苍白干燥，严重的贫血患者甚至出现指甲断裂以及杵状指的症状。"},{"title":"心脑血管异常。","content":"长期重度贫血患者可能出现心绞痛、心力衰竭，甚至诱发脑梗、心梗、心律失常等心脑血管疾病。"},{"title":"神经系统异常。","content":"严重贫血可出现神志模糊或痴呆等表现，导致头痛、头晕、肢体麻木以及感觉障碍等表现。"},{"title":"消化系统异常。","content":"严重贫血可能导致慢性萎缩性胃炎、胆道结石、舌萎缩、异食癖、吞咽困难等表现。"},{"title":"泌尿生殖系统异常。","content":"贫血可能诱发肾功能减退甚至衰竭，导致性功能减退，还可能导致不孕不育的发生。"},{"title":"内分泌系统异常。","content":"长期贫血可能导致甲状腺、性腺、肾上腺、胰腺的功能，导致内分泌失调甚至引发严重的疾病。"}],"display_name":"血红蛋白不足的危害"}]',0,0,'翁劲','翁劲','2022-05-16 21:02:35.0');
INSERT INTO ak_risk (article_name,risk_name,risk_key,list_contents,detail_contents,`type`,deleted,create_user,update_user,updated)VALUES ('鹰瞳X-黄斑视力损伤-儿童-低风险','macularVisualImpairment','macularVisualImpairment','[{"show": true,"type": "NAME","content": ["黄斑视力损伤风险"],"display_name": "风险名称"},{"show": true,"type": "SHORT_ADVICE","content": ["您的黄斑视力损伤风险为低风险，说明成年后出现高度近视和视力下降的可能性低。建议继续保持良好的用眼习惯。"],"display_name": "列表页建议"}]','[{"show":false,"type":"INTRO","content":["人类接受的信息中，90%都来自视觉，失明对人的工作和生活有极大影响。据统计，我国因视力低下而残疾的人数达1230万人，约近1分钟就会出现1个盲人、3个低视力患者。除了白内障、青光眼、年龄相关性黄斑病变、病理性近视等眼部疾病，糖尿病等全身慢病的并发症也会导致失明。"],"display_name":"简介"},{"show":false,"type":"FACTOR","content":[{"title":"近视尤其是高度近视。","content":"39%的高度近视人群在75岁之前有严重的视力损伤。"},{"title":"眼部病变家族史。","content":"如青光眼家族史。"},{"title":"高度用眼。","content":"经常使用电脑、手机等电子产品的人群，黄斑容易受到屏幕蓝光的损害。"},{"title":"40岁以上人群。","content":"青光眼、黄斑病变等致盲疾病的发生率较高。"}],"display_name":"引发原因"},{"show":false,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"补充叶黄素。","content":"具有黄斑变性风险的人群，每日可补充10毫克。"},{"title":"食用富含维生素A的食物。","content":"比如各种动物的肝脏、鱼肝油、奶类和蛋类，植物性的食物，比如胡萝卜、苋菜、菠菜、韭菜、青椒、红心白薯以及水果中的桔子、杏子、柿子等。"}]},{"type":"SPORT","title":"运动","content":[{"title":"高度近视者不能剧烈运动。","content":"高度近视者不能参加危险刺激的娱乐活动（例如蹦极、过山车等），避免视网膜脱落的风险。"}]},{"type":"EYE","title":"用眼","content":[{"title":"连续写字、阅读每40分钟须休息5-10分钟。","content":"避免眼睛过度疲劳。"},{"title":"不在晃动的车厢内阅读，不躺着看书。","content":""},{"title":"看电视时眼睛与电视保持屏幕尺寸约6~8倍的距离。","content":"每隔30分钟休息5~10分钟，连续看电视绝对不可以超过1小时。"},{"title":"不要在黑暗中看手机和电脑。","content":""}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":false,"type":"SYMPTOM","content":["视野缺失","视物变形","眼前黑点","视物模糊","眼痛眼胀"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]',0,0,'翁劲','翁劲','2022-06-23 21:13:44.0');
