





-- 黄斑视力损伤
UPDATE ak_risk
	SET detail_contents='[{"show":true,"type":"INTRO","content":["黄斑视力损伤是因用眼习惯等因素导致黄斑结构改变从而出现视力下降的问题。人类接受的信息中90%来自视觉，而黄斑是人体的视觉中心，黄斑区是视力最敏锐的地方，大多数致盲性疾病均与黄斑有关，黄斑视力受损会在很大程度上影响视物功能。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"年龄。","content":"黄斑的结构会随着年龄的增长而逐渐出现退化。"},{"title":"不良用眼习惯。","content":"不良的用眼习惯如近距离看书、在黑暗或摇晃的环境中玩手机也是导致黄斑视力受损的主要原因之一。"},{"title":"蓝光损伤。","content":"手机、电脑等电子屏幕的蓝光是加速黄斑视力损伤的因素。"},{"title":"营养缺乏。","content":"饮食不均衡导致的叶黄素等营养元素缺乏会导致黄斑对蓝光的防护能力降低，从而导致黄斑视力的损伤。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"补充叶黄素和玉米黄素。","content":"能够改善眼部功能，缓解视疲劳，每日可补充10毫克。"},{"title":"食用富含维生素A的食物。","content":"比如各种动物的肝脏、鱼肝油、奶类和蛋类，植物性的食物，比如胡萝卜、苋菜、菠菜、韭菜、青椒、红心白薯以及水果中的桔子、杏子、柿子等。"}]},{"type":"SPORT","title":"运动","content":[{"title":"","content":"运动时可适当使用太阳眼镜、帽子、遮阳伞等帮助抵御紫外线，减少对眼睛的刺激。"},{"title":"","content":"建议坚持每天至少2小时以上的室外活动，有助于保护视力，降低视力风险。"}]},{"type":"EYE","title":"用眼","content":[{"title":"连续写字、阅读每40分钟须休息5-10分钟。","content":"每天在家用眼时间（如看书、写字、弹钢琴等）以不超过两小时为原则，以免眼睛过度疲劳。"},{"title":"不在晃动的车厢内阅读，不躺着看书，不要在黑暗中看手机和电脑。","content":""},{"title":"看电视时眼睛保持与电视画面尺寸约6-8倍的距离。","content":"每隔30分钟休息5-10分钟，连续看电视绝对不可超过1小时以上。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["视野缺失","视物变形","视物模糊","视物疲劳"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]'
	WHERE id in (195,196,197,198,219,220,221,222);
-- 脑肿瘤
UPDATE ak_risk
	SET detail_contents='[{"show":true,"type":"INTRO","content":["脑肿瘤是对于颅内肿瘤的俗称，属于神经系统肿瘤。我国每10万人中就有10人患有脑肿瘤，其中半数为恶性肿瘤，可发生于任何年龄，如不及时治疗可能导致神经功能受损，晚期甚至危及生命。80%的脑肿瘤会造成视网膜的明显改变，因此从视网膜能一定程度预测脑肿瘤的风险。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"电离辐射","content":"长期受到电离辐射可能会诱发脑细胞的癌变。"},{"title":"感染因素","content":"病毒感染会诱发基因突变，增加癌变的风险。"},{"title":"发育异常","content":"在胚胎发育过程中残留在脑中的细胞会生长成肿瘤。"},{"title":"亚硝酸胺类","content":"染发剂以及食物中的亚硝酸胺类（咸菜，咸肉等），会增加患脑癌的风险。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"少吃重口味食物。","content":"减少酸菜、咸菜、咸肉等腌制类食品和烧烤类食品的过量食用，少吃口味过重的食物，避免过冷或过热食物的食用，不吃变质、过期的食物。"},{"title":"均衡营养。","content":"多吃西红柿、葡萄、苹果、胡萝卜、香菇、玉米等食物，增加维生素，提高免疫力，抗癌抗氧化。"}]},{"type":"SPORT","title":"运动","content":[{"title":"","content":"坚持体育锻炼。增强体质，保持良好的运动习惯，增强免疫力。"}]},{"type":"POLLUTION","title":"用眼","content":[{"title":"远离电磁辐射。","content":"在生活中避免接触过量的电磁辐射，睡觉时不要把手机放在枕头下。"},{"title":"避免频繁使用染发剂。","content":"远离有毒有害的化学物质。"},{"title":"保证充足的睡眠。","content":"保持积极乐观的心态，避免长期精神紧张，不吸烟，不饮酒。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["恶心呕吐","视乳头水肿","神经功能障碍","癫痫"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]'
	WHERE id in (223,224);
-- 猝死
UPDATE ak_risk
	SET detail_contents='[{"show":true,"type":"INTRO","content":["猝死是在短时间内因潜在的自然疾病突然发作或恶化而发生的突然死亡。在我国，每年有180万人发生猝死，平均每分钟就有3~4人猝死。资料显示，近90%猝死者发病后无法及时得到专业的急救，所以提前预防非常重要。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"心源性猝死。","content":"猝死中有75%为心源性猝死。冠心病、急性心梗等心脏原因可以迅速导致心律失常甚至心脏停搏，如不能及时得到急救，极易导致猝死。高血压、高血脂、既往心脏病史等疾病因素以及熬夜、吸烟等不良生活习惯都是心源性猝死的危险因素。"},{"title":"其他慢病相关。","content":"脑出血、代谢紊乱等都是导致猝死的危险因素，另外，暴饮暴食、酗酒等不良生活习惯也可能诱发猝死。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"少油少盐。","content":"成人每天食盐不超过6g，每天烹调油25-30g，少吃腌制食品。"},{"title":"多吃蔬菜水果。","content":"每日吃蔬菜400-500g，水果100g。"},{"title":"少吃红肉。","content":"红肉包括猪肉、牛肉、羊肉等，可适量多吃鱼肉、禽肉等白肉。"}]},{"type":"WEIGHT","title":"体重","content":[{"title":"控制BMI。","content":"成年人正常体质指数(BMI)为18.5-23.9，在24-27.9为超重，提示需要控制体重；BMI>28 为肥胖，需要减重。肥胖主要是通过血压影响心血管疾病的发生，是猝死的长期影响因素。"}]},{"type":"SPORT","title":"运动","content":[{"title":"坚持运动。","content":"保持运动习惯，运动强度适中，可根据身体状况选择合适的运动方式和运动强度。工作中减少久坐。"}]},{"type":"OTHERS","title":"其它","content":[{"title":"戒烟。","content":"吸烟人群发生心梗的风险比非吸烟人群提高1.54倍。"},{"title":"保暖。","content":"高血压人群在冬季要注意保暖，并谨慎泡温泉和桑拿，预防急性心脑血管事件。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["胸闷心悸","心绞痛","烦躁","恶心呕吐","心律失常"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]'
	WHERE id in (225,226,227,228);
-- 可吸入颗粒物
UPDATE ak_risk
	SET detail_contents='[{"show":true,"type":"INTRO","content":["可吸入颗粒物是指直径在10微米以下的颗粒物，通常能在环境空气中存在很长时间，烟草、雾霾、油烟等可吸入颗粒物进入呼吸系统后，较大的颗粒会沉积在肺部，而较小的颗粒会通过肺泡进入血液系统，从而损伤血管和神经。"],"display_name":"简介"},{"show":true,"type":"FACTOR","content":[{"title":"沙尘","content":"空气中的沙尘或扬尘等含有大量可吸入颗粒物。"},{"title":"工业污染","content":"工业生产时排放的废气、煤烟、汽车的尾气等都含有大量可吸入颗粒物。"},{"title":"吸烟","content":"烟草燃烧时会产生可吸入颗粒物的污染。"},{"title":"油烟","content":"烹调时产生的油烟是室内可吸入颗粒物的主要来源。"}],"display_name":"引发原因"},{"show":true,"type":"ADVICE","content":[{"type":"DIET","title":"饮食","content":[{"title":"营养均衡。","content":"注意营养合理的搭配，可以吃一些容易消化的食物，比如大米粥，蔬菜粥，瘦肉粥以及小白菜，白菜，西兰花，雪梨，苹果等。"},{"title":"清淡饮食。","content":"注意清淡的饮食，不要吃辛辣刺激的食物，不要吃太过油腻的食物，生冷的食物尽量的少吃。"}]},{"type":"SPORT","title":"运动","content":[{"title":"坚持运动。","content":"有氧运动有助于改善心肺功能，增强体质，提高血氧饱和度。选择合适自己的项目，循序渐进，量力而行，同时也应注意持之以恒。"}]},{"type":"OTHERS","title":"其它","content":[{"title":"远离烟草。","content":"不吸烟，远离二手烟，能够有效减少颗粒物被吸入的风险。"},{"title":"减少吸入。","content":"减少在工厂、施工现场、化工厂等附近活动，做饭时减少油烟的吸入。雾霾天可佩戴口罩从而减少颗粒物的吸入，雾霾天减少户外活动。"}]}],"default_url":"https://img3.airdoc.com/cms/salesman/05dc2235-4bc8-45bd-b357-84bd0c8eea30.png","display_name":"预防建议"},{"show":true,"type":"SYMPTOM","content":["气促气喘","咳嗽哮喘","呼吸困难"],"default_url":"https://img3.airdoc.com/cms/salesman/60f9472e-cf5e-4fd4-a5e9-827bf4258445.png","display_name":"常见表现"}]'
	WHERE id in (233,234,235,236);


